import './App.css';
import { useState, useEffect } from "react";

function App() {

  const [registerData,setRegisterData] = useState({user:"",password:""})
  const [loginData,setLoginData] = useState({user:"",password:""})
  const [allUsers,setAllUsers] = useState([])
  const [approve,setApprove]=useState(false)

  function newUser(){
    fetch('http://localhost:3500/register',{
      method:'POST',
      headers:{
        'Content-Type':'application/json'
      },
      body: JSON.stringify({
        user:registerData.user,
        pwd:registerData.password
      })
    })
    .then(response => response.json())
    .then(data => console.log(data))
  }

  function logIn(){
    fetch('http://localhost:3500/auth',{
      method:'POST',
      headers:{
        'Content-Type':'application/json'
      },
      body: JSON.stringify({
        user:loginData.user,
        pwd:loginData.password
      })
    })
    .then(response => response.json())
    .then(data => console.log(data))
    .then(setApprove(true))
  }


  return (
    <div className="App">
      <div className='signUp'>
        <input type="text" name="Ruser" value={registerData.user} 
          onChange={e=>setRegisterData((x)=>({...x, user:e.target.value}))}></input>
        <input type="text" name="Rpwd" value={registerData.password } 
          onChange={e=>setRegisterData((x)=>({...x, password:e.target.value}))}></input>
        <button onClick={newUser}>register</button>
      </div>

      <div className='login'>
        <input type="text" name="Luser" value={loginData.user} onChange={e=>setLoginData((x)=>({...x, user:e.target.value}))}></input>
        <input type="text" name="Lpwd" value={loginData.password} onChange={e=>setLoginData((x)=>({...x, password:e.target.value}))}></input>
        <button onClick={logIn}>login</button>

      </div>
      <button setApprove={false}>logOut</button>
    </div>
  );
}

export default App;
