import './App.css';
import { useState, useEffect } from "react";
import axios from "axios";
import { usePlaidLink } from 'react-plaid-link';
// import PlaidToken from './PlaidToken';


function PlaidToken() {
  axios.defaults.baseURL ="http://localhost:3500/plaid"

  const [linkToken, setLinkToken] = useState('');

  useEffect(() => {
    async function fetch() {
      const response = await axios.post("/create_link_token");
      setLinkToken(response.data.link_token);
      console.log("respnse", response.data)
    }
    fetch();
  }, []);

  const x = linkToken

  const config= {
      token: linkToken,
      onSuccess: (public_token, metadata) => {
          console.log("yaaa")    
      }
  }

  const c = usePlaidLink(config);

// // open Link immediately when ready
// React.useEffect(() => {
//   if (ready) {
//     open();
//   }
// }, [ready, open]);

  return (
      // <button onClick={() => open()} disabled={!ready}>
      //     Connect a bank account
      // </button>
      <p>{x}</p>
  );
}  

function App() {

  const [registerData,setRegisterData] = useState({user:"",password:""})
  const [loginData,setLoginData] = useState({user:"",password:""})
  const [allUsers,setAllUsers] = useState([])
  const [approve,setApprove]=useState(false)

  function newUser(){
    fetch('http://localhost:3500/register',{
      method:'POST',
      headers:{
        'Content-Type':'application/json'
      },
      body: JSON.stringify({
        user:registerData.user,
        pwd:registerData.password
      })
    })
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(err=>alert("Username already taken"))
  }

  function logIn(){
    fetch('http://localhost:3500/auth',{
      method:'POST',
      headers:{
        'Content-Type':'application/json'
      },
      body: JSON.stringify({
        user:loginData.user,
        pwd:loginData.password
      })
    })
    .then(response => {
      return response.json()
    })
    .then(data => {
      console.log(data)
      setApprove(true)
    })
    .catch(err => alert("Invalid username or password"))
  }


  return (
    <div className="App">
      <div className='signUp'>
        <h1>Sign Up</h1>
        <input type="text" name="Ruser" value={registerData.user} 
          onChange={e=>setRegisterData((x)=>({...x, user:e.target.value}))}></input>
        <input type="text" name="Rpwd" value={registerData.password } 
          onChange={e=>setRegisterData((x)=>({...x, password:e.target.value}))}></input>
        <button onClick={newUser}>register</button>
      </div>

      <div className='login'>
        <h1>Log In</h1>
        <input type="text" name="Luser" value={loginData.user} onChange={e=>setLoginData((x)=>({...x, user:e.target.value}))}></input>
        <input type="text" name="Lpwd" value={loginData.password} onChange={e=>setLoginData((x)=>({...x, password:e.target.value}))}></input>
        <button onClick={logIn}>login</button>
      </div>
      <br></br>

      <button onClick={()=>setApprove(false)}>logOut</button>
      {approve?
      <h1>You're In</h1>: null}
      <PlaidToken></PlaidToken>
    </div>
  );
}

export default App;
