import { useState, useEffect } from "react";
import {Link} from "react-router-dom"


export default function Login(){

    const [loginData,setLoginData] = useState({user:"",password:""})

    function logIn(){
        fetch('http://localhost:3500/auth',{
          method:'POST',
          headers:{
            'Content-Type':'application/json'
          },
          body: JSON.stringify({
            user:loginData.user,
            pwd:loginData.password
          })
        })
        .then(response => response.json())
        .then(data => {
          console.log(data)
        })
        .catch(err => alert("Invalid username or password"))
    }

    return(
    <div className='login'>
        <h1>Log In</h1>
        <input type="text" name="Luser" value={loginData.user} onChange={e=>setLoginData((x)=>({...x, user:e.target.value}))}></input>
        <input type="text" name="Lpwd" value={loginData.password} onChange={e=>setLoginData((x)=>({...x, password:e.target.value}))}></input>
        <button onClick={logIn}>login</button>
        <span>Don't Have an account <Link className="font-lg light-blue"to="/register">Sign Up</Link></span>
    </div>
    )


}
