import { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import Transactions from "./Transactions";

export default function NewGroup({currentUser,reRender}){
    const [groupName,setGroupName]= useState("")
    const [currUser,setCurrUser]= useState(currentUser)


    function createGroup(){
        let gpName;
        if(groupName==""){
            gpName="Finance Flyers"
        }
        else{
            gpName=groupName
        }
        console.log("current User from in")
        console.log(currUser)
        const url = `http://localhost:3500/group/new`;
        fetch(url, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body:JSON.stringify({
                "user":currUser,
                "groupName":gpName
            }),
        })
        .then(response => response.json())
        .then(data => {
            console.log(data)
            reRender()
        })
        .catch(err => console.log(err));
    }





    return(
        <div className="flex-col">
            <div>
            <input className="rounded-md focus:outline-none border-2 p-1.5 placeholder-ml-4 border-purple" placeholder="Finance Flyers" type="text" name="Luser" value={groupName} onChange={e=>setGroupName(e.target.value)}></input>
            </div>
            <div><label>Group Name</label></div>
            <button onClick ={createGroup}className="rounded-md bg-purple px-4 py-1 text-white mb-8  mt-2">Create New Group</button>
        </div>
    ) 
}
